    @Slot(int, int, float)
    def getNewPosAngle(self, ve, vd, angle):
        angle_rad = math.radians(angle)
        deltaX = math.cos(angle_rad)*(ve + vd)/2
        deltaY = math.sin(angle_rad)*(ve + vd)/2
        deltaAngle = (ve - vd)/1.5

        newX = self.posX + (deltaX * self.deltaT)
        newY = self.posY + (deltaY * self.deltaT)
        newAngle = angle + (deltaAngle * self.deltaT)
        
        return newX, newY, newAngle

    @Slot()
    def resetSimulation(self):
        pass

    @Slot(list)
    def updateSimulation(self, data: list):
        Ae = int(data[0])
        Se = int(data[1])*2-1

        Ad = int(data[2])
        Sd = int(data[3])*2-1

        ve = Ae * Se * self.vel
        vd = Ad * Sd * self.vel

        xewX, xewY, newAngle = self.getNewPosAngle(ve, vd, self.angle)

        self.robo.setPos(xewX, xewY)
        self.robo.setRotation(newAngle)


        pass

